const display=document.querySelector(".display"),buttons=document.querySelectorAll("button");buttons.forEach(button=>{button.addEventListener("click",function(){let btnId=button.id;if("="===btnId)try{let result=eval(display.value);result%1!=0?display.value=result.toFixed(2):display.value=result}catch(e){display.value="Write a correct expression"}else if("C"===btnId)display.value="";else if("Del"===btnId)display.value=display.value.slice(0,-1);else if("sqrt"===btnId){if(display.value)try{let result=Math.sqrt(parseFloat(display.value));display.value=result.toFixed(2)}catch(e){display.value="Invalid input"}}else display.value+=btnId})});